import React, { Component } from "react";
import img0 from '../assets/img/img0.jpg';
import img1 from '../assets/img/img1.jpg';
import img2 from '../assets/img/img2.jpg';
import img3 from '../assets/img/img3.jpg';
import img4 from '../assets/img/img4.jpg';


class PhotoCard extends Component{
    render(){
        return(
            <div className="card" style={{ width: "18rem" }}>
            <img src={this.props.photo} className="card-img-top" alt="..." />
            <div className="card-body">
              <p className="card-text">
               {this.props.title}
              </p>
            </div>
          </div>
        )
    }
}


class CardState extends Component {
    constructor(props){
        super(props)
        this.state={
            photo1:img0,
            photo2:img1,
            photo3:img2,
            title1:"Nature is super",
            title2:"Nature is good",
            title3:"Nature is beutiful"
        }
    }
  render() {
    return (
      <div>

        <div className="con-btn mb-5">
            <div className="btn btn-secondary mx-3">Nature One</div>
            <div className="btn btn-secondary mx-3">Nature Two</div>
            <div className="btn btn-secondary mx-3">Nature Three</div>
            <div className="btn btn-secondary mx-3">Nature Four</div>
        </div>

       <div className="row gap-3">
        <PhotoCard photo={img0} title= {this.state.title1}/>
        <PhotoCard photo={img1} title = {this.state.title2}/>
        <PhotoCard photo={img2} title = {this.state.title1}/>
       </div>
      </div>
    );
  }
}

export default CardState;
